import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/UserProfile.css';

function UserProfile({ user, setUser }) {
    const [profile, setProfile] = useState(null);
    const [updatedProfile, setUpdatedProfile] = useState({});
    const [otpSent, setOtpSent] = useState(false);
    const [otp, setOtp] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        if (user) {
            fetch(`http://localhost:5001/user-profile?email=${user.email}`)
                .then((response) => response.json())
                .then((data) => setProfile(data))
                .catch((error) => console.error('Error fetching profile:', error));
        }
    }, [user]);

    const handleChange = (e) => {
        setUpdatedProfile({
            ...updatedProfile,
            [e.target.name]: e.target.value,
        });
    };

    const handleSave = () => {
        fetch('http://localhost:5001/user-profile', {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: user.email, ...updatedProfile }),
        })
            .then((response) => response.json())
            .then((data) => {
                setProfile(data);
                alert('Profile updated successfully!');
            })
            .catch((error) => console.error('Error updating profile:', error));
    };

    const handleLogout = () => {
        setUser(null);
        navigate('/');
        alert('Logged out successfully!');
    };

    const handleSendOtp = () => {
        // Send OTP to user's email
        axios
            .post('http://localhost:5001/send-otp', { email: user.email, subscription: 'year' })
            .then((response) => {
                setOtpSent(true);
                alert('OTP sent to your email!');
            })
            .catch((error) => console.error('Error sending OTP:', error));
    };

    const handleOtpValidation = () => {
        axios
            .post('http://localhost:5001/validate-otp', { email: user.email, otp, subscription: 'year' })
            .then((response) => {
                alert('Subscription updated successfully!');
            })
            .catch((error) => {
                console.error('Error validating OTP:', error);
                alert('Invalid OTP');
            });
    };

    // Redirect to landing page if user is not logged in
    if (!user) {
        navigate('/');
    }

    return (
        <div className="user-profile">
            <h2>User Profile</h2>
            {profile ? (
                <div className="profile-form">
                    <label>
                        Name:
                        <input
                            type="text"
                            name="name"
                            value={updatedProfile.name || profile.name}
                            onChange={handleChange}
                        />
                    </label>
                    <label>
                        Email:
                        <input
                            type="email"
                            name="email"
                            value={profile.email}
                            disabled
                        />
                    </label>
                    <label>
                        Phone:
                        <input
                            type="text"
                            name="phone"
                            value={updatedProfile.phone || profile.phone || ''}
                            onChange={handleChange}
                        />
                    </label>
                    <label>
                        Address:
                        <input
                            type="text"
                            name="address"
                            value={updatedProfile.address || profile.address || ''}
                            onChange={handleChange}
                        />
                    </label>

                    {/* Show Send OTP button if user is logged in */}
                    {user && !otpSent && (
                        <button onClick={handleSendOtp}>Send OTP</button>
                    )}

                    {/* OTP Input and validation */}
                    {otpSent && (
                        <div>
                            <label>
                                Enter OTP:
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={(e) => setOtp(e.target.value)}
                                />
                            </label>
                            <button onClick={handleOtpValidation}>Validate OTP</button>
                        </div>
                    )}

                    <button onClick={handleSave}>Save Changes</button>
                    <button onClick={handleLogout} className="logout-button">
                        Logout
                    </button>
                </div>
            ) : (
                <p>Loading profile...</p>
            )}
        </div>
    );
}

export default UserProfile;
