import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./Component/LandingPage";
import ChatbotComponent from "./Component/ChatbotComponent";
import AboutPage from "./Component/AboutPage";  // Assuming this component exists
import ContactPage from "./Component/ContactPage";  // Assuming this component exists
import Navbar from "./Component/Navbar";  // Import Navbar
import Footer from "./Component/Footer"; // Import Footer
import ThemeProvider from "./Component/ThemeContext"; // Assuming this manages light/dark themes
import "./App.css";

function App() {
  const [showChatbot, setShowChatbot] = useState(false);

  const handleChatbotOpen = () => {
    setShowChatbot(true);
  };

  const handleChatbotClose = () => {
    setShowChatbot(false);
  };

  return (
    <ThemeProvider>
      <Router>
        <div className="App">
          {/* Include Navbar at the top level */}
          <Navbar />

          <Routes>
            <Route
              path="/"
              element={<LandingPage onChatbotOpen={handleChatbotOpen} />}
            />
            <Route path="/chatbot" element={<ChatbotComponent />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
          </Routes>

          {/* Display Chatbot */}
          {showChatbot && (
            <div className="chatbot-wrapper">
              <ChatbotComponent />
              <button className="close-chatbot-button" onClick={handleChatbotClose}>
                Close Chatbot
              </button>
            </div>
          )}
          <Footer />
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
