import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./Component/LandingPage";
import ChatbotComponent from "./Component/ChatbotComponent";
import AboutPage from "./Component/AboutPage";
import ContactPage from "./Component/ContactPage";
import Navbar from "./Component/Navbar";
import Footer from "./Component/Footer";
import SignUpPage from "./Component/SignUpPage";
import LoginPage from "./Component/LoginPage";
import ThemeProvider, { useTheme } from "./Component/ThemeContext";
import "./App.css";

function App() {
  const { theme } = useTheme(); // Get the current theme from context
  const [user, setUser] = useState(null); // Manage the logged-in user state

  // Apply the theme dynamically to the <body> element
  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <Router>
      <div className="App">
        {/* Navbar with user state */}
        <Navbar user={user} setUser={setUser} />

        {/* Main content */}
        <main className="content">
          <Routes>
            <Route path="/" element={<LandingPage user={user} />} />
            <Route path="/chatbot" element={<ChatbotComponent />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/signup" element={<SignUpPage setUser={setUser} />} />
            <Route path="/login" element={<LoginPage setUser={setUser} />} />
          </Routes>
        </main>

        {/* Footer */}
        <Footer />
      </div>
    </Router>
  );
}

// Wrap the App component with the ThemeProvider
function AppWrapper() {
  return (
    <ThemeProvider>
      <App />
    </ThemeProvider>
  );
}

export default AppWrapper;
