import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";  // Import Routes and Route
import LandingPage from "./Component/LandingPage";
import ChatbotComponent from "./Component/ChatbotComponent";
import "./App.css";

function App() {
  const [showChatbot, setShowChatbot] = useState(false);

  const handleChatbotOpen = () => {
    setShowChatbot(true);
  };

  const handleChatbotClose = () => {
    setShowChatbot(false);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Define route for LandingPage */}
          <Route path="/" element={<LandingPage onChatbotOpen={handleChatbotOpen} />} />
          {/* Define route for ChatbotComponent */}
          <Route path="/chatbot" element={<ChatbotComponent />} />
        </Routes>

        {/* Conditional rendering for chatbot */}
        {showChatbot && (
          <div className="chatbot-wrapper">
            <ChatbotComponent />
            <button className="close-chatbot-button" onClick={handleChatbotClose}>
              Close Chatbot
            </button>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
