import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./Component/LandingPage";
import ChatbotComponent from "./Component/ChatbotComponent";
import ThemeProvider from "./Component/ThemeContext";  // Assuming this manages light/dark themes
import "./App.css";

function App() {
  const [showChatbot, setShowChatbot] = useState(false);

  const handleChatbotOpen = () => {
    setShowChatbot(true);
  };

  const handleChatbotClose = () => {
    setShowChatbot(false);
  };

  return (
    <ThemeProvider>
      <Router>  {/* Wrap everything with Router for navigation */}
        <div className="App">
          <Routes>
            {/* Define Routes for LandingPage and Chatbot */}
            <Route
              path="/"
              element={<LandingPage onChatbotOpen={handleChatbotOpen} />}
            />
            <Route
              path="/chatbot"
              element={
                <div className="chatbot-wrapper">
                  <ChatbotComponent />
                  <button
                    className="close-chatbot-button"
                    onClick={handleChatbotClose}
                  >
                    Close Chatbot
                  </button>
                </div>
              }
            />
          </Routes>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
