import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./Component/LandingPage";
import ChatbotComponent from "./Component/ChatbotComponent";
import AboutPage from "./Component/AboutPage";
import ContactPage from "./Component/ContactPage";
import Navbar from "./Component/Navbar";
import Footer from "./Component/Footer";
import ThemeProvider from "./Component/ThemeContext";
import "./App.css";

function App() {
  const [showChatbot, setShowChatbot] = useState(false);

  const handleChatbotOpen = () => {
    setShowChatbot(true);
  };

  const handleChatbotClose = () => {
    setShowChatbot(false);
  };

  return (
    <ThemeProvider>
      <Router>
        <div className="App">
          {/* Navbar at the top */}
          <Navbar />

          {/* Main content wrapped in Routes */}
          <main className="content">
            <Routes>
              <Route
                path="/"
                element={<LandingPage onChatbotOpen={handleChatbotOpen} />}
              />
              <Route
                path="/chatbot"
                element={
                  showChatbot ? (
                    <ChatbotComponent />
                  ) : (
                    <div>
                      <h2>Chatbot is not open yet.</h2>
                    </div>
                  )
                }
              />
              <Route path="/about" element={<AboutPage />} />
              <Route path="/contact" element={<ContactPage />} />
            </Routes>
          </main>

          {/* Display Chatbot Modal */}
          {showChatbot && (
            <div className="chatbot-modal">
              <div className="chatbot-wrapper">
                <ChatbotComponent />
                <button
                  className="close-chatbot-button"
                  onClick={handleChatbotClose}
                >
                  Close Chatbot
                </button>
              </div>
            </div>
          )}

          {/* Footer at the bottom */}
          <Footer />
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
