import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../css/Navbar.css";
import logo from "../assets/Logo.webp"; // Replace with your logo file path
import AccountCircleIcon from "@mui/icons-material/AccountCircle";

function Navbar({ setUser }) {
    // Initialize user state from localStorage or set to null
    const [user, setUserState] = useState(localStorage.getItem("user") || null);

    const handleLogout = () => {
        setUserState(null); // Set user state to null to log out
        localStorage.removeItem("user"); // Remove user from localStorage
        alert("Logged out successfully!");
    };

    // Update user in localStorage when user state changes
    useEffect(() => {
        if (user) {
            localStorage.setItem("user", user);
        }
    }, [user]);

    return (
        <nav className="navbar">
            <div className="navbar-logo">
                <img src={logo} alt="Chatbot Logo" />
            </div>
            <div className="navbar-links">
                <ul className="navbar-list">
                    <li className="navbar-item">
                        <Link to="/" className="navbar-link">Home</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/about" className="navbar-link">About</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/contact" className="navbar-link">Contact</Link>
                    </li>
                    {!user ? (
                        <>
                            <li className="navbar-item">
                                <Link to="/signup" className="navbar-link">Sign Up</Link>
                            </li>
                            <li className="navbar-item">
                                <Link to="/login" className="navbar-link">Login</Link>
                            </li>
                        </>
                    ) : (
                        <>
                            <li className="navbar-item">
                                <Link to="/profile" className="navbar-link">
                                    <AccountCircleIcon style={{ fontSize: "30px" }} />
                                </Link>
                            </li>
                            <li className="navbar-item">
                                <button className="navbar-link" onClick={handleLogout}>
                                    Logout
                                </button>
                            </li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
}

export default Navbar;
