import React, { useState } from "react";
import "../css/ContactPage.css"; // Import the CSS for styling

function ContactPage() {
    const [formData, setFormData] = useState({ name: '', email: '', message: '' });
    const [status, setStatus] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/submit', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });

            const result = await response.json();
            if (response.ok) {
                setStatus(result.message);
                setFormData({ name: '', email: '', message: '' });
            } else {
                setStatus(result.error);
            }
        } catch (error) {
            setStatus('An error occurred while submitting the form.');
        }
    };

    return (
        <div className="contact-page">
            <h1>Contact Us</h1>
            <p>If you have any questions, feedback, or suggestions, we'd love to hear from you!</p>
            <form className="contact-form" onSubmit={handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} placeholder="Enter your name" required />

                <label htmlFor="email">Email:</label>
                <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} placeholder="Enter your email" required />

                <label htmlFor="message">Message:</label>
                <textarea id="message" name="message" value={formData.message} onChange={handleChange} rows="5" placeholder="Write your message here" required></textarea>

                <button type="submit">Submit</button>
            </form>
            {status && <p>{status}</p>}
        </div>
    );
}

export default ContactPage;
