import React, { useState } from "react";
import "../css/PricingPage.css";

function PricingPage({ setUser }) {
    const [selectedSubscription, setSelectedSubscription] = useState('free');
    const [otp, setOtp] = useState('');
    const [status, setStatus] = useState('');
    const [otpSent, setOtpSent] = useState(false); // To track if OTP has been sent

    const handleSubscriptionSelect = (subscription) => {
        setSelectedSubscription(subscription);
    };

    const handleOtpChange = (e) => {
        setOtp(e.target.value);
    };

    const handleSendOtp = async () => {
        // Ensure that the user has selected a subscription
        if (!selectedSubscription) {
            setStatus("Please select a subscription.");
            return;
        }

        try {
            const response = await fetch('http://localhost:5001/send-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: "user@example.com", // Replace with actual email
                    subscription: selectedSubscription
                }),
            });

            if (response.ok) {
                setOtpSent(true); // Mark OTP as sent
                setStatus("OTP sent successfully. Please check your inbox.");
            } else {
                const errorMessage = await response.text();
                setStatus(`Error: ${errorMessage}`);
            }
        } catch (error) {
            setStatus('An error occurred while sending OTP. Please try again.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!otp) {
            setStatus("OTP is required.");
            return;
        }

        try {
            const response = await fetch('http://localhost:5001/validate-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: "user@example.com", // Replace with actual email
                    otp,
                    subscription: selectedSubscription
                }),
            });

            if (response.ok) {
                const user = await response.json();
                setUser(user); // Set the subscription data in user state
                alert('Subscription updated successfully!');
            } else {
                const errorMessage = await response.text();
                setStatus(`Error: ${errorMessage}`);
            }
        } catch (error) {
            setStatus('An error occurred while updating subscription. Please try again.');
        }
    };

    return (
        <div className="pricing-page">
            <h1>Choose Your Subscription</h1>
            <div className="pricing-options">
                <div
                    className={`pricing-option ${selectedSubscription === 'free' ? 'selected' : ''}`}
                    onClick={() => handleSubscriptionSelect('free')}
                >
                    <h2>Free</h2>
                    <p>1-Day Chatbot Access</p>
                </div>
                <div
                    className={`pricing-option ${selectedSubscription === 'year' ? 'selected' : ''}`}
                    onClick={() => handleSubscriptionSelect('year')}
                >
                    <h2>1-Year</h2>
                    <p>Full Access for 1 Year</p>
                </div>
            </div>

            <button onClick={handleSendOtp} disabled={otpSent}>
                {otpSent ? "OTP Sent" : "Send OTP"}
            </button>

            {otpSent && (
                <form onSubmit={handleSubmit}>
                    <label htmlFor="otp">Enter OTP:</label>
                    <input
                        type="text"
                        id="otp"
                        value={otp}
                        onChange={handleOtpChange}
                        placeholder="Enter OTP"
                        required
                    />
                    <button type="submit">Confirm Subscription</button>
                </form>
            )}

            {status && <p>{status}</p>}
        </div>
    );
}

export default PricingPage;
