import { createChatBotMessage } from "react-chatbot-kit";
import CustomMessage from "../Component/CustomMessage"; // A custom message component if needed
import LearningOption from '../Component/LearningOption';
import LinkList from "../Component/LinkList";

import React from "react";
const config = {
  botName: "ReactBot",
  initialMessages: [createChatBotMessage(`Hello! How can I help you today?`,{
  widget : "learningOptions" }),
  ],
  customComponents: {
    botMessageBox: (props) => <div className="bot-message-box">{props.children}</div>,
    userMessageBox: (props) => <div className="user-message-box">{props.children}</div>,
  },
  customStyles: {
    botMessageBox: {
      backgroundColor: "#376B7E",
    },
    chatButton: {
      backgroundColor: "#5ccc9d",
    },
  },
  widgets: [
     {
         widgetName: "learningOptions",
        widgetFunc: (props) => <LearningOption {...props} />,
     },
     {
      widgetName: "javascriptLinks",
      widgetFunc: (props) => <LinkList {...props} />,
      props: {
        options: [
          {
            text: "Introduction to JS",
            url:
              "https://www.freecodecamp.org/learn/javascript-algorithms-and-data-structures/basic-javascript/",
            id: 1,
          },
          {
            text: "Mozilla JS Guide",
            url:
              "https://developer.mozilla.org/en-US/docs/Web/JavaScript/Guide",
            id: 2,
          },
          {
            text: "Frontend Masters",
            url: "https://frontendmasters.com",
            id: 3,
          },
        ],
      },
    },
    {
      widgetName: "dataVisualizationLinks",
      widgetFunc: (props) => <LinkList {...props} />,
      props: {
        options: [
          {
            text: "Introduction to D3",
            url: "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",
            id: 1,
          },
          {
            text: "InfoVis",
            url: "XXXXXXXXXXXXXXXXXXXXXX",
            id: 2,
          },
          {
            text: "Frontend Masters",
            url: "XXXXXXXXXXXXXXXXXXXXXXXXXXX",
            id: 3,
          },
        ],
      },
    }
 ],
  customMessages: {
    // Define custom messages if you have any custom message components
    custom: (props) => <CustomMessage {...props} />,
  },
};

export default config;
